    "basedOn"      : for src                 make  tgt.basedOn = reference( src )
    "status"       : for src                 make  tgt.status = "draft"
    "authoredOn"   : for src                 make  tgt.authoredOn = evaluate( src, now() )
    "ad.code"      : for src.code     as vvv make  tgt.reasonCode = vvv
